import React, { Component , useEffect, useState} from 'react';
import axios from 'axios';
import './App.css';
// import { nanoid } from 'nanoid';
// import JsonDataDisplay from './Table.js';
// import Table from './Table.js';
// import JsonData from './data.json'; // https://api.jsonserve.com/1ekkzf
// import JsonNewData from "./newData.json";
// import PersonList from './PersonList.js';
// import Form from './Form.js';


export async function deleteDataServ(id) {
  try {
    const response = await axios.delete(`http://178.128.196.163:3000/api/records/${id}`);
    return response.data;
  }
  catch (error)
  {
    console.log('Error', error.message);
  }
};

export async function putDataServ (row) {
  try {
    let response = await axios.put('http://178.128.196.163:3000/api/records', row);
  }
  catch (error){
    console.log('Error', error.message);
  }
  // let result = await response.json();
  // setContacts(response);//?? 
};

function App() {

  const [contacts, setContacts] = useState([]);

  const [addFormData, setAddFormData] = useState(
    {
      name: "",
      age: "",
      email: "",
    }
  );

async function getDataServ () {
  try {
    let response = await axios.get('http://178.128.196.163:3000/api/records');
    console.log(response.data);
    setContacts(response.data); 
  }
  catch (error) {
      console.log('Error', error.message);
  }
};

  useEffect(()=>{
    getDataServ(); // вызывается один раз
  }, []);

    // getDataServ(); // вызывается каждую секунду?

    async function deleteDataServ(contactId) {
      try {
        const response = await axios.delete(`http://178.128.196.163:3000/api/records/${contactId}`);
        // return response.data;
        if (response.status == 200)
        {
          const newContacts = [...contacts];

          const index = newContacts.findIndex(item => item._id === contactId);

          newContacts.splice(index, 1); // удалить в newContacts начинаю с номера index один элемент
          setContacts(newContacts);
        }
      }
      catch (error)
      {
        console.log('Error', error.message);
      }
    };

  const addForm = (event) => {
    event.preventDefault();  // отмена события
    const fieldName = event.target.getAttribute('name'); // что лежит в инпуте под name - это name and age and email
    const fieldValue = event.target.value; 

    const newFormData = {...addFormData}; // копирует с возсожностью расширения
    newFormData[fieldName] = fieldValue; //заполняем массив новыми значениями
    // console.log(newFormData);

    setAddFormData(newFormData); // обновляем наш  addFormData, изначально был пустым
  };

  const addFormSubmit = (event) => {
    event.preventDefault();// нужно для того чтобы при нажатии на кнопку страница не обновлялась

    // console.log(Object.keys(JsonData).length);
    // const getMaxIndex = () => {
    //   var max = contacts[0].id;
    //   for (var i = 0; i < contacts.length; i++)
    //   {
    //     if (contacts[i].id > max)
    //       max = contacts[i].id;
    //   }
    //   return max;
    // }
    // var max = getMaxIndex();
  // console.log(max);


    const newRow = {
      data :
      {
        name: addFormData.name,
        age: addFormData.age,
        email: addFormData.email,
      },
    };

    putDataServ(newRow); // посылаем на сервер наше новое значение
// console.log(newRow);
    const newContact = [...contacts, newRow]; // добавляем в массив contacts новую строку
    setContacts(newContact); // обновляем данные
    console.log("concat: ", contacts);
    // console.log(newContact);
    // console.log(typeof(contacts));
  };

  const deleteFunc = (contactId) => {
    deleteDataServ(contactId);
  };

    return (
      <div className="container">
         <h1>MyApp Table</h1> 
          <div>
            <table className="table table-striped">
                <thead>
                    <tr>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Email</th>
                    </tr>
                </thead>
                 <tbody>
                    {contacts.map((info)=>(
                    <tr key={info._id}>
                        <td>{info.data.name}</td>
                        <td>{info.data.age}</td>
                        <td>{info.data.email}</td>
                        <td><button className='btn btn-primary' onClick={()=>deleteFunc(info._id)}>Delete</button></td>
                    </tr>

                      ))}
                </tbody>

            </table>

          </div>
          <h2>Add a contact</h2>
          <form className="form-inline" onSubmit={addFormSubmit}>
          <div className="form-group">
            <input type="text" name="name" className='form-control form-inline_input'  placeholder='Your name' onChange={addForm}/>
            <input type="text" name="age" className='form-control form-inline_input'  placeholder='Your age' onChange={addForm}/>
            <input type="text" name="email" className='form-control form-inline_input'  placeholder='Your email' onChange={addForm}/>
            <button type="submit" className='btn btn-primary' >Add</button>
            </div>
			     </form>
      </div>
    );
}

export default App;
